        clearTouchTargets();
        resetCancelNextUpFlag(this);
        mGroupFlags &= ~FLAG_DISALLOW_INTERCEPT;	// FLAG_DISALLOW_INTERCEPT 用来阻止父类拦截点击事件
        mNestedScrollAxes = SCROLL_AXIS_NONE;
    }
